mapper.select:
select count(*)
from member
where userId=#{userId} and password=#{password}

select *
from member
where name=#{name} and tel=#{tel}

select count(*)
from member
where userId=#{userId} and name=#{name} and tel=#{tel}

mapper.update:
update member
set password=#{password}
where userId=#{userId}

mapper.select:
select password
from member
where userId=#{id}

select count(*)
from member
where userid=#{userId} and password=#{password}
